mapper.select:
SELECT album.*
FROM favorAlbum,album
WHERE userId = #{param1}
    AND favorAlbum.albumId = album.albumId;

SELECT music.*
FROM favorMusic,music
WHERE userId = #{param1}
    AND favorMusic.musicId = music.musicId;

SELECT singer.*
FROM followSinger,singer
WHERE userId = #{param1}
    AND followSinger.singerId = singer.singerId;

SELECT user.*
FROM userRelation,user
WHERE userRelation.userId = #{param1}
    AND userRelation.followerId = singer.singerId;

SELECT user.*
FROM userRelation,user
WHERE userRelation.userId = #{param1}
    AND userRelation.userId = singer.singerId;
